package config

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestCertConfig(t *testing.T) {
	assert := assert.New(t)
	cfg := NewTestConfig()

	cert := &Cert{
		Name: "me.dev",
		cfg:  cfg,
	}
	defer func() {
		_ = cert.Delete()
	}()

	err := cert.Fetch()
	assert.Nil(err)
	assert.Empty(cert.Key)
	assert.Empty(cert.Cert)

	keyValue := ""
	certValue := ""

	cert.Key = keyValue
	cert.Cert = certValue

	err = cert.Save()
	assert.Nil(err)

	nCert := &Cert{
		Name: cert.Name,
		cfg:  cfg,
	}
	err = nCert.Fetch()
	assert.Nil(err)
	assert.Equal(keyValue, nCert.Key)
	assert.Equal(certValue, nCert.Cert)
}

func TestCerts(t *testing.T) {
	assert := assert.New(t)
	certs := make(Certs, 0)
	name := "me.dev"
	certs = append(certs, &Cert{
		Name: "me.dev",
	})

	assert.Equal(name, certs.Get(name).Name)
	assert.Nil(certs.Get("foo"))
}
